RSpec.describe Api::V1::SheetManagersController, :type => :controller do
  it_behaves_like "api_controller"

  # This should return the minimal set of attributes required to create a valid
  # SheetManager. As you add validations to SheetManager, be sure to
  # adjust the attributes here as well.
  let(:valid_attributes) {
    {
      year: 2000,
      sheet_id: sheet.id,
    }
  }

  let(:invalid_attributes) {
    { year: nil, sheet_id: nil }
  }

  let!(:sheet) { create(:sheet) }
  let!(:sheet_manager) { create(:sheet_manager) }

  describe "GET index" do
    it "assigns all sheet_managers as @sheet_managers" do
      get :index, { format: :json }
      expect(assigns(:sheet_managers)).to eq([sheet_manager])
    end
  end

  describe "GET show" do
    it "assigns the requested sheet_manager as @sheet_manager" do
      get :show, { id: sheet_manager.id, format: :json }
      expect(assigns(:sheet_manager)).to eq(sheet_manager)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new SheetManager" do
        expect {
          post :create, { sheet_manager: valid_attributes, format: :json }
        }.to change(SheetManager, :count).by(1)
      end

      it "assigns a newly created sheet_manager as @sheet_manager" do
        post :create, { sheet_manager: valid_attributes, format: :json }
        expect(assigns(:sheet_manager)).to be_a(SheetManager)
        expect(assigns(:sheet_manager)).to be_persisted
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved sheet_manager as @sheet_manager" do
        post :create, { sheet_manager: invalid_attributes, format: :json }
        expect(assigns(:sheet_manager)).to be_a_new(SheetManager)
      end

      it "re-renders the 'new' template" do
        post :create, { sheet_manager: invalid_attributes }
        expect(response.status).to eq(422)
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      let(:new_attributes) {
        {
          year: 3000,
          sheet: build(:sheet),
        }
      }

      it "updates the requested sheet_manager" do
        put :update, { id: sheet_manager.id, sheet_manager: new_attributes }
        sheet_manager.reload
        expect(sheet_manager.year).to eq(3000)
      end

      it "assigns the requested sheet_manager as @sheet_manager" do
        put :update, { id: sheet_manager.id, sheet_manager: valid_attributes }
        expect(assigns(:sheet_manager)).to eq(sheet_manager)
