    end

    describe "with invalid params" do
      it "assigns the sheet_manager as @sheet_manager" do
        put :update, { id: sheet_manager.id, sheet_manager: invalid_attributes }
        expect(assigns(:sheet_manager)).to eq(sheet_manager)
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested sheet_manager" do
      expect {
        delete :destroy, { id: sheet_manager.id, format: :json }
